import Barcode from "./Barcode"
import { useState } from "react"
const Items = ({items, barcodeNumber}) => {
    const [view, setView] = useState(false)
    return(
        <>
             <div className="card" >
                <div className="card-body">
                    <h5 className="card-title">{items.company_name}</h5>
                    <h6 className="card-subtitle mb-2 text-muted">{items.company_address}</h6>
                    <h6 className="card-subtitle mb-2 text-muted">{items.company_contact}</h6>
                    <h6 className="card-subtitle mb-2 text-muted">총 물건 수: {items.basket_items.length}</h6>
                    <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>    
                </div>
            </div>
            <button type="button" className="btn btn-outline-primary" onClick={() => {
                setView(!view)
            }}>바코드</button>
            {
                view ? (<Barcode barcodeNumber={barcodeNumber}/>) : (<></>)
            }
        </>


    )
}

export default Items;
