import axios from "axios"
import {useNavigate} from "react-router"
import { useState} from "react";

const SignInForm = ({signInData, onChangeSignInData}) => {

    // const [cookies,setCookie,removeCookie] = useCookies(["userData"]);
    const navigate = useNavigate();
    const [errorMessage,setErrorMessage] = useState("");
    const [data, setData] = useState();
    const onClick = async (req, res, next) => {
        try {
        const response = await axios.get(
            'http://3.35.213.205/api/ft/login',{
                params:{
                    userid:signInData.userid,
                    passwd:signInData.passwd
                }
            }
        );
            setData(response.data);
            if(response.data.code === 200){
                navigate("/list", {state: response.data.sessionid})
            }

        } catch (e) {
            console.log(e);
        }
    };
    return (
        <div className="album">
            <div className="container">
                <form>
                    <div className="mb-3">
                        <label htmlFor="userid" className="form-label">Id</label>
                        <input type="email"  className="form-control" value={signInData.userid} onChange={onChangeSignInData} name="userid" id="userid" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="passwd" className="form-label">Password</label>
                        <input type="password" className="form-control" value={signInData.passwd} onChange={onChangeSignInData} name="passwd" id="passwd"/>
                    </div>
                    <div>
            <button type="button" className="btn btn-lg btn-secondary fw-bold border-white bg-black" onClick={onClick} >Login</button>
        </div>
                </form>
            </div>
        </div>
    )
}

export default SignInForm;
