// import $ from "jquery"
import axios from "axios"
import {useNavigate} from "react-router-dom"
import { useState} from "react";
// import {useCookies} from "react-cookie"

const SignInForm = ({signInData, onChangeSignInData}) => {

    // const [cookies,setCookie,removeCookie] = useCookies(["userData"]);
    const navigate = useNavigate();
    const [errorMessage,setErrorMessage] = useState("");
    const [data, setData] = useState(null);
    const onClick = async () => {
        try {
        const response = await axios.get(
            'http://3.35.213.205/api/ft/login.php',{
                params:{
                    userid:signInData.userid,
                    passwd:signInData.passwd
                }
            }
        );
            setData(response.data);
            console.log(response.data);
            if(response.data.code === 200){
                navigate("/list")
            }
        } catch (e) {
            console.log(e);
        }
    };
    const onClickLoginButton = async () => {
        // if(signInData.email === ""){
        //     alert("이메일을 입력해주세요");
        //     $("#email").focus()
        //     return;
        // }
        // if(signInData.password === ""){
        //     alert("비밀번호를 입력해주세요");
        //     $("#password").focus()
        //     return;
        // }

        sendSignInData().then(res => {
            // console.log(res)
            // setCookie("userData",res.data,{path:"/"});
            alert("로그인이 완료되었습니다.")
            navigate("/list")
        }).catch(e => {
            // console.log("e",e)
            // console.log("e.response.data.fail",e.response.data.fail)
            // console.log("e.response.data.error",e.response.data.error)
            setErrorMessage(e.response.data.fail)
        }).finally(()=>{
            // console.log(cookies.userData)
        })
    }

    const sendSignInData = async () => {
        return await axios.post(`http://3.35.213.205/api/ft/login.php/user/login`,signInData)
    }    

    return (
        <div className="album">
            <div className="container">
                <form>
                    <div className="mb-3">
                        <label htmlFor="email" className="form-label">Email address</label>
                        <input type="email"  className="form-control" value={signInData.userid} onChange={onChangeSignInData} name="email" id="email" aria-describedby="emailHelp" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password</label>
                        <input type="password" className="form-control" value={signInData.passwd} onChange={onChangeSignInData} name="password" id="password"/>
                    </div>
                    <div className="mb-3">
                        <p className="text-danger">

                            errorMessage
                        </p>
                    </div>
                    <button type="button"  className="btn btn-primary" onClick={onClickLoginButton}>로그인</button>
                    <div>
            <button onClick={onClick}>불러오기</button>
        </div>

        {/* {data && (
            <textarea
                rows={7}
                value={JSON.stringify(data, null, 2)}
                readOnly={true}
            />
        )} */}
                </form>
            </div>
        </div>
    )
}

export default SignInForm;
