import $ from "jquery"
import axios from "axios"
import {useNavigate} from "react-router-dom"
import { useState} from "react";
// import {useCookies} from "react-cookie"

const SignInForm = ({signInData, onChangeSignInData}) => {

    // const [cookies,setCookie,removeCookie] = useCookies(["userData"]);
    const navigate = useNavigate();
    const [errorMessage,setErrorMessage] = useState("");
    // const [data, setData] = useState(null);
    // const onClick = async () => {
    //     try {
    //     const response = await axios.get(
    //         'http://3.35.213.205/api/ft/login',{
    //             params:{
    //                 userid:signInData.userid,
    //                 passwd:signInData.passwd
    //             }
    //         }
    //     );
    //         setData(response.data);
    //         if(response.data.code === 200){
    //             navigate("/list")
    //         }

    //     } catch (e) {
    //         console.log(e);
    //     }
    // };
    const onClickLoginButton = async () => {
        if(signInData.email === ""){
            alert("이메일을 입력해주세요");
            $("#email").focus()
            return;
        }
        if(signInData.password === ""){
            alert("비밀번호를 입력해주세요");
            $("#password").focus()
            return;
        }

        sendSignInData().then(res => {
            console.log(res)
            // setCookie("userData",res.data,{path:"/"});
            alert("로그인이 완료되었습니다.")
            // navigate("/list")
        }).catch(e => {
            // console.log("e",e)
            // console.log("e.response.data.fail",e.response.data.fail)
            // console.log("e.response.data.error",e.response.data.error)
            setErrorMessage(e.response.data.fail)
        }).finally(()=>{
            // console.log(cookies.userData)
        })
    }

    const sendSignInData = async () => {
        return await axios.get(`http://3.35.213.205/api/ft/login`,{
                params:{
                    userid:signInData.userid,
                    passwd:signInData.passwd
                }})
    }    

    return (
        <div className="album">
            <div className="container">
                <form>
                    <div className="mb-3">
                        <label htmlFor="userid" className="form-label">Id</label>
                        <input type="email"  className="form-control" value={signInData.userid} onChange={onChangeSignInData} name="userid" id="userid" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="passwd" className="form-label">Password</label>
                        <input type="password" className="form-control" value={signInData.passwd} onChange={onChangeSignInData} name="passwd" id="passwd"/>
                    </div>
                    <div>
            <button type="button" className="btn btn-lg btn-secondary fw-bold border-white bg-black" onClick={onClickLoginButton} >Login</button>
        </div>
                </form>
            </div>
        </div>
    )
}

export default SignInForm;
