import { useEffect, useState } from "react"
import "../../style.css"
import axios from "axios";
import Barcode from "./Barcode"
import { useLocation } from "react-router";

const List = () => {
    const [data, setData] = useState({});
    const { state } = useLocation();

    useEffect(() => {
      axios.get(
        'http://3.35.213.205/api/ft/orderlist',{
          headers:{
            'X-Ft-Sessionid':state
          }
        }).then(res => {
          setData(res.data)
        })
    },[data])
    return (
        <>
        {
          Array.isArray(data.orderitems) && 
          ( 
            data.orderitems.map((items, index) => {
              const itemStatus = {
                "0": <><sapn className="status">주문 완료</sapn><br/><sapn className="barcodeimg"><Barcode barcodeNumber={state}/></sapn></>,
                "100": <><sapn className="status100">패킹 시작</sapn><br/><sapn className="username">{items.ft_username}</sapn></>,
                "200": <><sapn className="status200">패킹 중</sapn><sapn className="username">{items.ft_username}</sapn></>,
                "300": <><sapn className="status300">패킹 완료</sapn><br/><sapn className="card-text">{items.ft_username}</sapn></>
              }
              return (
                <>
                  <div className="orderList" keys={index}>
                    <div className="textArea">
                    <br/>
                    <p className="title">{items.company_name} ({items.basket_items.length} 품목)</p>
                    <p className="address">{items.company_address}</p>
                    <p className="contact">{items.company_contact}</p>
                    </div>
                    <div className="statusArea">
                    {
                      itemStatus[items.status]
                    }
                    </div>
                  </div>
                </>
              )
            }))
        }
    </>
    )
}

export default List
