import axios from "axios"
import {useNavigate} from "react-router"
import { useState} from "react";
// import {useCookies} from "react-cookie"

const SignInForm = ({signInData, onChangeSignInData}) => {

    // const [cookies,setCookie,removeCookie] = useCookies(["userData"]);
    const navigate = useNavigate();
    const [errorMessage,setErrorMessage] = useState("");
    const [data, setData] = useState();
    const onClick = async (req, res, next) => {
        try {
        const response = await axios.get(
            'login',{
                params:{
                    userid:signInData.userid,
                    passwd:signInData.passwd
                }
            }
        );
            setData(response.data);
            console.log(response.data)
            console.log(data)
            if(response.data.code === 200){
                navigate("/list", {state: response.data.sessionid})
            }

        } catch (e) {
            console.log(e);
        }
    };
    // const onClickLoginButton = async () => {
    //     sendSignInData().then(res => {
    //         console.log(res)
    //         alert("로그인이 완료되었습니다.")
    //         // navigate("/list")
    //     }).catch(e => {
    //         console.log("e: ",e)
    //         // setErrorMessage(e.response.data.fail)
    //     })
    // }

    // const sendSignInData = async () => {
    //     return await axios.post(`http://3.35.213.205/api/ft/login`,{
    //         param:{
    //             userid:signInData.userid,
    //             passwd:signInData.passwd
    //     }},{
    //         headers:{
    //             'Access-Control-Allow-Origin': '*'
    //         }
    //     })
    // }

    return (
        <div className="album">
            <div className="container">
                <form>
                    <div className="mb-3">
                        <label htmlFor="userid" className="form-label">Id</label>
                        <input type="email"  className="form-control" value={signInData.userid} onChange={onChangeSignInData} name="userid" id="userid" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="passwd" className="form-label">Password</label>
                        <input type="password" className="form-control" value={signInData.passwd} onChange={onChangeSignInData} name="passwd" id="passwd"/>
                    </div>
                    <div>
            <button type="button" className="btn btn-lg btn-secondary fw-bold border-white bg-black" onClick={onClick} >Login</button>
        </div>
                </form>
            </div>
        </div>
    )
}

export default SignInForm;
