import { useEffect, useState } from "react"
import Barcode from "./Barcode"
import axios from "axios";
import { useLocation } from "react-router";

const List = () => {
    const [view, setView] = useState(false)
    const [data, setData] = useState({});
    const {state} = useLocation();
  //   const onClick = async () => {
  //     try {
  //       const response = await axios.get(
  //         'http://3.35.213.205/api/ft/orderlist',{
  //           headers:{
  //             'x-ft-sessionid':state
  //           }
  //         })
  //           setData(response.data);
  //       } catch (e) {
  //           console.log(e);
  //       }
  // };
  useEffect(() => {
    axios.get(
      'http://3.35.213.205/api/ft/orderlist',{
        headers:{
          'X-Ft-Sessionid':state
        }
      }).then(res => {
        console.log(res.data)
        setData(res.data)
      })
  },[])

    return (
        <>
        <div>
            {/* { data && data.orderItems } */}
            주문 리스트
            <button type="button" className="btn btn-outline-primary" onClick={() => {
                setView(!view)
            }}>바코드</button>
            <br />
            {
                view ? (<Barcode barcodeNumber={state}/>) : (<></>)
            }
        </div>
        {
          Array.isArray(data.orderitems) && 
          ( 
            data.orderitems.map((el, index) => {
              return (
                <>
                  <div key={index}>{el.company_name}</div>
                </>
              )
            }))
        }
    <div>
      {data && (
        <textarea
          rows={7}
          value={JSON.stringify(data.orderitems, null, 2)}
          readOnly={true}
        />
      )}
    </div>
    </>
    )
}

export default List
