import Barcode from "./Barcode"
// import { useState } from "react"
const Items = ({items, barcodeNumber}) => {
    // const [view, setView] = useState(false)
    return(
        <>
        <div className="album py-5 bg-light">
            <div className="container-fluid">
                <div className="row row-col-xs-3">
                    <div className="col">
                    <div className="card shadow-sm">
                        <div className="card-body">
                        <h5 className="card-title">{items.company_name} ({items.basket_items.length} 품목)</h5>
                                <h6 className="card-subtitle mb-2 text-muted">{items.company_address}</h6>
                                <h6 className="card-subtitle mb-2 text-muted">{items.company_contact}</h6>
                                {
                                    items.status === "0" ? (<><p className="card-text">주문 완료</p><Barcode barcodeNumber={barcodeNumber}/></>) 
                                    : (items.status === "100" ? (<><p className="card-text">작업장 할당 완료</p><p className="card-text">{items.ft_username}</p></>) 
                                    : (items.status === "200" ? (<><p className="card-text">패킹 작업 진행</p><p className="card-text">{items.ft_username}</p></>)
                                    : (items.status === "300" ? (<><p className="card-text">패킹 완료</p><p className="card-text">{items.ft_username}</p></>)
                                    :(<></>)))) 
                                }
                        {/* <button type="button" className="btn btn-outline-primary" style={{"marginRight":"40px"}} onClick={() => {
                            setView(!view)
                        }}>바코드</button>
                        {
                            view ? (<Barcode barcodeNumber={barcodeNumber}/>) : (<></>)
                        } */}
                        </div>
                    </div>
                    </div>
                </div>
                </div>
            </div>
        </>
    )
}

export default Items;
